package SeleniumclassManish;

public class addsubmuldiv {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     add();
     sub();
     mul();
     div();
     mod();
	
	}
	static void add() {
	int a=100;
	int b=200;
	int sum=a+b;
	System.out.println(sum);
	}
	
	static void sub() {
		int a=100;
		int b=200;
		int sub=a-b;
		System.out.println(sub);
		
	}
	static void mul() {
		int a=100;
		int b=200;
		int mul=a*b;
		System.out.println(mul);
		
	}
	static void div() {
		int a=100;
		int b=200;
		float div=a/b;
		System.out.println(div);}
	
	static void mod() {
		int a=100;
		int b=200;
		float mod=a/b;
		System.out.println(mod);}
}
